mapper.delete:
delete
from ploughthewaves.userandoj2
where uid = (
    select id
    from ploughthewaves.user
    where openid = #{openid}
) and ojtype = #{ojType} and oj_username = #{ojUsername};

mapper.select:
select ojtype, oj_username, all_solved_number
from ploughthewaves.user join ploughthewaves.userandoj2 on user.id = userandoj2.uid
where user.openid = #{openid};

select uid,ojtype,oj_username,all_solved_number,all_contest_number,upd_time
from ploughthewaves.user join ploughthewaves.userandoj2 on user.id = userandoj2.uid
where user.openid = #{openid};

update.foreach:
update ploughthewaves.userandoj2
all_solved_number = #{item.allSolvedNumber}, now_rating = #{item.nowRating}, max_rating = #{item.maxRating}, recent_max_rating = #{item.recentMaxRating}, recent_solved_number = #{item.recentSolvedNumber}, all_contest_number = #{item.allContestNumber}, recent_contest_number = #{item.recentContestNumber}, upd_time = #{item.updTime}
where uid = #{item.uid} and ojtype = #{item.ojType} and oj_username = #{item.ojUsername}
